mapper.select:



		AND title like concat('%', #{query.title}, '%')


		AND business_type = #{query.businessType}


	    AND business_type in

 			#{businessType}



		AND status = #{query.status}


		AND oper_name like concat('%', #{query.operName}, '%')


		and date_format(oper_time,'%y%m%d') >= date_format(#{query.params.beginTime},'%y%m%d')


		and date_format(oper_time,'%y%m%d') <= date_format(#{query.params.endTime},'%y%m%d')


order by oper_id desc
